/**
 * create the prefix comment of the generated file
 *
 * @param disabledFormat whether disabled format
 */
export function createPrefixCommentOfGenFile(disabledFormat = true) {
  const prefix = `/* prettier-ignore */
`;

  let comment = `/* eslint-disable */
// Generated by elegant-router
// Read more: https://github.com/mufeng889/elegant-router
// Vue auto route: https://github.com/soybeanjs/elegant-router
// 请不要手动修改此文件，否则会导致优雅路由无法正常工作
// 如果需要修改，请在优雅路由配置文件中进行修改
// 这是自动生成的文件，请不要手动修改
`;

  if (disabledFormat) {
    comment = prefix + comment;
  }

  return comment;
}
